target.script:
function print(){
	var args = [];
	for(var x=0; x<arguments.length; x++){
		args.push(arguments[x]);
	}
	java.lang.System.out.println.apply(java.lang.System.out, args);

}

function echo(msg, parentTask){
	var echo = dojo.createTask("echo");
	parentTask.addTask(echo);
	echo.setMessage(msg);
}

